'use strict';

// get current list from flow
let list = flow.get('productsList');

// if current item is not already in the list, add it.
if (!list.has(msg.rfid_code)) {
    list.set(msg.rfid_code, {
        name: msg.payload.uid.name,
        amount: 0,
    });
}

// add or remove the item
switch (msg.payload.direction) {
    case 'in':
        list.get(msg.rfid_code).amount += 1;
        break;
    case 'out':
        list.get(msg.rfid_code).amount -= 1;
        break;
}

// save list to flow
flow.set('productsList', list);

return [
    // LED
    {},
    // Server update
    {"rfid_code": msg.rfid_code, "payload": list.get(msg.rfid_code).amount},
     // node red purchase list display
    {'payload': Array.from(list, ([rfid, value]) => ({title: msg.rfid_code, description: value.name +" : " + value.amount}))}
];
